import {BrowserRouter, Routes, Route} from 'react-router-dom'
import Home from './pages/Home/Home'
import Tab1 from './pages/Tab 1/Tab1';
import Tab3 from './pages/Tab 3/Tab3';
import { Info,Context } from './controller/info'
import React, {useState,useEffect,useContext} from 'react';

export default function App() {
  console.log("App Load")
  const [userData, setUserData] = useState(() => {
    const user = sessionStorage.getItem("user");
    console.log(sessionStorage)
    console.log(user)
    return JSON.parse(user) || null;
  });

  useEffect(() => {
    console.log("userData updated, persist state");
    sessionStorage.getItem("user", JSON.stringify(userData));
  }, [userData]);

  return (
    <Info value = {{userData,setUserData}}>
    <div>
      <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/tab1" element={<Tab1/>}/>
        <Route path="/tab3" element={<Tab3/>}/>
        <Route path="*" element={null} />
      </Routes>
      </BrowserRouter>
    </div>
    </Info>
  );
}
