import { useMemo } from 'react';
import {
  MaterialReactTable,
  // useMaterialReactTable,
  MRT_ActionMenuItem
} from 'material-react-table';
import { Box, IconButton } from '@mui/material';
import {Edit as EditIcon,Delete as DeleteIcon,} from '@mui/icons-material';
import { useState } from 'react';

const initialData = [
  {
    main: 'Main Ranking 1',
    add: 'Additional Ranking 1',
    weight: 0.5,
  },
];

const Table = () => {
  const [data, setData] = useState(initialData);
  const columns = useMemo(
    () => [
      {
        accessorKey: 'main',
        header: 'Main Rankings',
        size: 200,
        enableEditing: false,
      },
      {
        accessorKey: 'add',
        header: 'Additional Rankings',
        size: 200,
        enableEditing: false,

      },
      {
        accessorKey: 'weight', 
        header: 'Weights (0 to 1)',
        size: 200,
        enableEditing: true,
      },
    ],
    [],
  );

  return <MaterialReactTable
          columns={columns}
          data={data}
          enableRowActions
          // renderRowActionMenuItems={({ row, table }) => [
          //   <MRT_ActionMenuItem 
          //   icon={<EditIcon />}
          //   key="edit"
          //   label="Edit"
          //   onClick={() => console.info('Edit')}
          //   table={table}
          //   />,
          //   <MRT_ActionMenuItem
          //   icon={<DeleteIcon />}
          //   key="delete"
          //   label="Delete"
          //   onClick={() => console.info('Delete')}
          //   table={table}
          //   />,
          // ]}
          renderRowActions={({ row, table }) => (
            <Box sx={{ display: 'flex', flexWrap: 'nowrap', gap: '8px' }}>
              {/* <IconButton
                color="primary"
                onClick={() => {
                  table.setEditingRow(row);
                  data[row.index] = row;
                  setData([...data]);
                }}
              >
                <EditIcon />
              </IconButton> */}
              <IconButton
                color="error"
                onClick={() => {
                  data.splice(row.index, 1); 
                  setData([...data]);
                }}
              >
                <DeleteIcon />
              </IconButton>
            </Box>
          )}
        />
};

export default Table;
